const {send} = require("../restRequestHelper");
const HttpMethod = require("../http-method");

async function getDialogById({accountId, token, conversationId, dialogId}) {

    const path = `/v1/conversations/${conversationId}/dialogs/${dialogId}`;

    return await send({accountId, token, path, method: HttpMethod.GET});
}

module.exports = getDialogById;
