const {send} = require("../restRequestHelper");
const HttpMethod = require("../http-method");

async function getSingleParticipant({accountId, token, agentId, conversationId, dialogId}) {

    const path = `/v1/conversations/${conversationId}/dialogs/${dialogId}/participants/${agentId}`;

    return await send({accountId, token, path, method: HttpMethod.GET});
}

module.exports = getSingleParticipant;
