const {send} = require('../restRequestHelper');
const HttpMethod = require('../http-method');

async function getUrlForDownloadFile({accountId, token, relativePath}) {

    const path = `/v1/brands/${accountId}/file-download-path`;

    const data = {
        relativePath
    };

    return await send({accountId, token, path, data, method: HttpMethod.POST})
}

module.exports = getUrlForDownloadFile;
