const {send} = require('../restRequestHelper');
const HttpMethod = require('../http-method');

async function getUrlForUploadFile({accountId, token, fileSize, fileType}) {

    const path = `/v1/brands/${accountId}/file-upload-path`;

    const data = {
        fileSize,
        fileType
    };

    return await send({accountId, token, path, data, method: HttpMethod.POST});
}

module.exports = getUrlForUploadFile;
