const requestTypeV2 = '.ams.aam.UnsubscribeExConversations';
const requestTypeV3 = 'cqm.UnsubscribeExConversations';

/**
 * @private
 * @param subscriptionId
 * @returns {{type: unsubscribeExConversations.type, body: {subscriptionId}}}
 */
function unsubscribeExConversations(subscriptionId) {
    if (subscriptionId === undefined || subscriptionId === null) {
        throw new Error('subscriptionId cannot be null or undefined');
    }
    return {
        type: this.type,
        body: {
            subscriptionId: subscriptionId
        }
    };
}

module.exports = {
    v2: unsubscribeExConversations.bind({type: requestTypeV2}),
    v3: unsubscribeExConversations.bind({type: requestTypeV3})
};

