const {updateConversationField, UpdateConversationFieldName, v2Context} = require('./update-conversation-field');

/**
 * @private
 * @param conversationId {string}
 * @param time {Number}
 * @returns {Object}
 */
function manualETTRUpdate(conversationId, time) {

    const data = {
        field: UpdateConversationFieldName.ManualETTR,
        time
    };

    return updateConversationField.call(this, conversationId, data);
}

module.exports = {
    v2: manualETTRUpdate.bind(v2Context)
}
