const {updateConversationField, UpdateConversationFieldName, v2Context} = require('./update-conversation-field');

/**
 * @private
 * @param conversationId {uuid}
 * @param noteData {string}
 * @returns {object}
 */
function noteUpdate(conversationId, noteData) {
    const data = noteUpdateData(noteData);
    return updateConversationField.call(this, conversationId, data)
}

function noteUpdateData(noteData) {
    return {
        field: UpdateConversationFieldName.Note,
        note: noteData
    };
}

module.exports = {
    v2: noteUpdate.bind(v2Context),
}
