const {updateConversationField, UpdateConversationFieldName, v2Context, v3Context} = require('./update-conversation-field');

/**
 * @private
 * @param conversationId {string}
 * @param skillId {string}
 * @returns {object}
 */
function skillChange(conversationId, skillId) {
    const data = skillChangeData(skillId);
    return updateConversationField.call(this, conversationId, [data])
}

function skillChangeData(skillId) {
    return {
        field: UpdateConversationFieldName.Skill,
        type: 'UPDATE',
        skill: skillId
    };
}

module.exports = {
    v2: skillChange.bind(v2Context),
    v3: skillChange.bind(v3Context),
    skillChangeData
}
