const {UpdateConversationFieldName, updateConversationField, v2Context, v3Context} = require('./update-conversation-field');

/**
 * @private
 * @param conversationId {string}
 * @param ttrType {TTRType}
 * @param value {string}
 * @returns {object}
 */
function ttrUpdate(conversationId, ttrType, value) {

    const data = {
        field: UpdateConversationFieldName.TTRField,
        ttrType,
        value
    };

    return updateConversationField.call(this, conversationId, data);
}

module.exports = {
    v2: ttrUpdate.bind(v2Context),
    v3: ttrUpdate.bind(v3Context)
}
