const requestTypeV2 = ".ams.ms.GenerateURLForDownloadFile";
const requestTypeV3 = "ms.GenerateURLForDownloadFile";

/**
 * @private
 * @returns {WsApiRequest}
 */
function getUrlForDownloadFile(relativePath) {
    return {
        type: this.type,
        body: {
            relativePath
        }
    };
}

module.exports = {
    v2: getUrlForDownloadFile.bind({type: requestTypeV2}),
    v3: getUrlForDownloadFile.bind({type: requestTypeV3})
};