const requestTypeV2 = ".ams.ms.GenerateURLForUploadFile";
const requestTypeV3 = "ms.GenerateURLForUploadFile";

/**
 * @private
 * @returns {WsApiRequest}
 */
function getUrlForUploadFile(fileSize, fileType) {
    return {
        type: this.type,
        body: {
            fileSize,
            fileType
        }
    };
}

module.exports = {
    v2: getUrlForUploadFile.bind({type: requestTypeV2}),
    v3: getUrlForUploadFile.bind({type: requestTypeV3})
};