const {publishEvent, PublishEventType, v2Context, v3Context} = require('./publish-event');

// https://confluence.liveperson.com/display/CS/ChatState+Event
/**
 * @private
 * @returns {WsApiRequest}
 */
function chatStateEvent(conversationId, dialogId, chatState) {
    return publishEvent.call(this, conversationId, dialogId, {
        type: PublishEventType.CHAT_STATE_EVENT,
        chatState
    });
}

module.exports = {
    v2: chatStateEvent.bind(v2Context),
    v3: chatStateEvent.bind(v3Context)
};
