const requestTypeV3 = "ms.SubscribeMessagingEvents";

/**
 * @private
 * @param conversationId
 * @param dialogId
 * @param fromSequence
 * @returns {WsApiRequest}
 */
function subscribeMessagingEvents(conversationId, dialogId, fromSequence = 0) {
    return {
        type: this.type,
        body: {
            conversationId,
            dialogId,
            fromSeq: fromSequence
        }
    };
}

module.exports = {
    // v2: there is no v2 for this function
    v3: subscribeMessagingEvents.bind({type: requestTypeV3})
};
