const requestTypeV2 = ".ams.brandprofile.GetBrandProfile";
const requestTypeV3 = "brandprofile.GetBrandProfile";

/**
 * @private
 * @returns {WsApiRequest}
 */
function getBrandProfile(brandId) {
    return {
        type: this.type,
        body: {
            brandId
        }
    };
}

module.exports = {
    v2: getBrandProfile.bind({type: requestTypeV2}),
    v3: getBrandProfile.bind({type: requestTypeV3})
};
