const requestTypeV2 = ".ams.userprofile.GetUserProfile";
const requestTypeV3 = "userprofile.GetUserProfile";

/**
 * @private
 * @returns {WsApiRequest}
 */
function getUserProfile(userId) {
    return {
        type: this.type,
        body: {
            userId
        }
    };
}

module.exports = {
    v2: getUserProfile.bind({type: requestTypeV2}),
    v3: getUserProfile.bind({type: requestTypeV3})
};
