const requestTypeV2 = '.ams.routing.SetAgentState';
const requestTypeV3 = 'rs.SetAgentState';
const ChannelType = require('../../../conversation/channel-type');

/**
 * @private
 * @returns {WsApiRequest}
 */
function setAgentState(agentId, agentState) {
    return {
        type: this.type,
        body: {
            availability: agentState,
            channels: [ChannelType.MESSAGING],
            agentUserId: agentId
        }
    };
}

module.exports = {
    v2: setAgentState.bind({type: requestTypeV2}),
    v3: setAgentState.bind({type: requestTypeV3})
};
