const requestTypeV2 = ".ams.routing.SubscribeRoutingTasks";
const { MESSAGING } = require('../../../conversation/channel-type');

/**
 * @private
 * @param {string} accountId
 * @param {string} agentId
 * @returns {WsApiRequest}
 */
function subscribeRoutingTasks(accountId, agentId) {
    return {
        type: this.type,
        body: {
            channelType: MESSAGING,
            brandId: accountId,
            agentId: agentId
        }
    };
}

module.exports = {
    v2: subscribeRoutingTasks.bind({type: requestTypeV2})
};
