const requestTypeV2 = ".ams.routing.UpdateRingState";

/**
 * @private
 * @param {string} ringId
 * @param {string} ringState
 * @returns {WsApiRequest}
 */
function updateRingState(ringId, ringState) {
    return {
        type: this.type,
        body: {
            ringId: ringId,
            ringState: ringState
        }
    };
}

module.exports = {
    v2: updateRingState.bind({type: requestTypeV2})
};
