// https://lpgithub.dev.lprnd.net/Architecture/api-ums/blob/master/api-ums-ms/src/main/java/com/liveperson/api/ams/ms/types/ChatState.java

/**
 * @typedef {string} ParticipantChatState
 */

/**
 * @enum {ParticipantChatState}
 * @category Conversation
 */
const ParticipantChatState = {
    ACTIVE: 'ACTIVE',
    INACTIVE: 'INACTIVE',
    GONE: 'GONE',
    COMPOSING: 'COMPOSING',
    PAUSE: 'PAUSE',
    /** Indicates that the client app is in background state, needs to be sent in order to get Push Notifications */
    BACKGROUND: 'BACKGROUND',
    /** an SDK only state, indicating that the participant has been removed from the dialog */
    REMOVED: 'REMOVED'
};
// TODO how are the other states reached? Does UMS send an "INACTIVE" chat state?

module.exports = ParticipantChatState;
